
public class ClaseMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// MATH. SQRT PARA CALCULA LA RAIZ DE UN NUMERO
		double raiz= Math.sqrt(10);
		System.out.println(raiz);	
		double numero= 10;
		float rai = (float)Math.sqrt(numero);
		System.out.println(rai);
		// MATH. ROUND PARA REDONDEAR UN NUMERO
		double mun1 = 5.65;
		int  resultado= (int)Math.round(mun1) ;
		System.out.println(resultado);
		//MATH. POW PARA SACAR EL CALCULO EN EXPOONENTE
		double base= 5;
		double exponente = 3;
		double resultad = Math.pow(base, exponente);
		System.out.println("exponente:" + " "+ resultad);
		
	}

}
